  const Axiosphoto = (value) => {
    const Command = value;
    console.log(Command);
    if (Command === "Errormask") {
      alert("กรุณาถอดหน้ากาก");
    } else if (Command === "FoundData") {
      const Data = ["3640600077526", "สมชาย", "หมายปอง"];
      dispatch(updateSsid(Data[0]));
      dispatch(updateFirstName(Data[1]));
      dispatch(updateSsid(Data[2]));
      alert("พบข้อมูล");
      Router.push("/page.covid4");
    } else if (Command === "NotFoundData") {
      alert("ไม่พบข้อมูล");
    }
  };

  function taskOne() {
    return new Promise(async (resolve) => {
      const A = async () => {
        const imageSrc = await webcamRef.current.getScreenshot();
        const blob = await fetch(imageSrc)
          .then((res) => {
            res.blob();
            setData(res);
            console.log("success 1",res);
            resolve(res); 
          })
          .catch(() => console.log("error"));
      };
      await A();
    });
  }
  function taskTwo() {
    return new Promise(async (resolve) => {
      const B = async () => {
        await console.log(taskOne.res);
        await console.log("2");
        /*const formData = new FormData();
      formData.append("picture", data);*/
        const a = "10";
        return a;
      };
      await B();
      await resolve();
    });
  }
  function taskThree() {
    return new Promise(async (resolve) => {
      const C = () => {
        //console.log("B", taskTwo.a);
        console.log("3");
      };
      await C();
      await resolve();
    });
  }

  const main = async () => {
    await taskOne();
    await taskTwo();
    await taskThree();
  };

  const capture = React.useCallback(async () => {
    //const imageSrc = await webcamRef.current.getScreenshot();
    main();
