        method:"post",
        url:"http://34.87.54.114:8003/api/v1/file/uploadphoto",
        headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          },
      data:{ picture:blob}
    }).then((res)=>{
        console.log('success',res)
    }).catch((error)=>{
        console.log('error',error)
    })*/
    //await console.log("1", data);
    //await Axiosphoto("Errormask");
  }, [webcamRef, setImgSrc]);
  /*useEffect(() => {
    const formData = new FormData();
    if (data === "") {
    } else {
      console.log(data);
      formData.append("picture.png", data);
      console.log(formData);
    }
  }, [data]);*/
  return (
    <div>
      <FormItem>
        <div>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            style={{ borderRadius: "20px" }}
          />
        </div>
        <Button onClick={capture} style={{ width: "100%" }} type="primary">
          Capture photo
        </Button>
        <div>
          <img src={imgSrc} />
        </div>
      </FormItem>
    </div>
  );
};

export default WebcamCapture;
