import {
    Form,
    Select,
    InputNumber,
    DatePicker,
    Switch,
    Slider,
    Button,
    Input,
  } from "antd";
  import Link from "next/link";
  import React ,{ useState,useEffect } from 'react'
  import HeaderCovid from "./components/component.headercovid";
  import QuestionCovid from './components/component.question'

  const FormItem = Form.Item;
function CovidQuestion(props) {
    //State จัดการ Button
    const [showResults, setShowResults] = useState(false);
    //State จัดการ เรื่องเวลาของเสียง
    //const { Timeduration } = useContext(CounterContext);
    //State จัดการเรื่องชุดคำถาม =========================
    const [page, setPage] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    //const [myAnswer, setmyAnswer] = useState(null);
    //const [keyword, setkeyword] = useState(["ใช่", "ไม่ใช่"]);
    const [score, setscore] = useState(0);
    const [disabled, setdisabled] = useState(true);
    const [isEnd, setisEnd] = useState(false);
    const [isLoading, setisLoading] = useState(false);
    const [options, setoptions] = useState([]);
    const [question, setQuestion] = useState("");
    const [answer, setAnswer] = useState("");
    // token
    const [token, settoken] = useState(false);
    /*let History = useHistory();*/
    //=================================================
  
    //Function โชว์ Button
    const ShowButton = () => {
      return (
        <div style={{ width: "100%" }}>
          <div style={{ textAlign: "center" }}>
            <Button
              onClick={() => {
                nextQuestionHandler("yes");
              }}
            >
              ใช่
            </Button>
            <Button
              onClick={() => {
                nextQuestionHandler("no");
              }}
            >
              ไม่ใช่
            </Button>
          </div>
        </div>
      );
    };
    //style component ของ Progress-------------
    /*const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
    const Box = styled.div`
      color: #fff !important;
      background-color: #ec6900 !important;
      height: 24px;
      width: 100%;
      animation: ${rotate} 52s linear;
    `;*/
    //-----------------------------------------
  
    useEffect(prevState => {
      if (!isLoading) {
        fetch("https://smxv3.sse.codesandbox.io/question")
          .then(response => response.json())
          .then(pages => {
            setPage(pages);
            setisLoading(true);
            settoken(true);
          });
      }
      if (token) {
        console.log("BAS", token);
        console.log("คำถามที่เท่าไร", currentQuestion);
        console.log("data", page);
        settoken(false);
        loadQuizData();
      }
      if (currentQuestion > 0) {
        if (currentQuestion !== currentQuestion - 1) {
          setQuestion(page[currentQuestion].text);
          setAnswer(page[currentQuestion].answer);
          setoptions(page[currentQuestion].keyword);
        }
      }
      if (!disabled) {
        setdisabled(true);
      }
    });
    //
  
    //Function การจัดการคำถาม==============================
    const loadQuizData = () => {
      setdisabled(false);
      setQuestion(page[currentQuestion].text);
      setAnswer(page[currentQuestion].answer);
      setoptions(page[currentQuestion].keyword);
      setShowResults(true);
      console.log("คำถามปัจจุบัน", question, answer, options);
    };
    const nextQuestionHandler = answer => {
      if (currentQuestion === page.length - 1) {
        finishHandler(answer);
      } else {
        if (answer === "yes") {
          setscore(score + 1);
        } else {
          setscore(score);
        }
        setCurrentQuestion(currentQuestion + 1);
        console.log("Question", currentQuestion, score);
      }
    };
  
    const finishHandler = answer => {
      if (currentQuestion === page.length - 1) {
        setisEnd(true);
      }
      if (answer === "yes") {
        setscore(score + 1);
      }
    };
    //====================================================
    if (isEnd) {
      console.log("จบบบบบบบบบบบบบบบบบบบบบบบบบ");
      /*History.push("/covid4");*/
      return null;
    } else {
      return (
          <FormItem>
        A
        </FormItem>
      );
    }
  }
  
  export default CovidQuestion;
