import { Form, Button } from "antd";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from "../public/9.png";
import Axios from "axios";
import AudioPlayer from "./components/component.audioplayer";
import Duration from "./components/component.duration";
import Speechtotext from "./components/component.speechtotext";

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [showResults, setShowResults] = useState(false);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);
  const [disabled, setdisabled] = useState(true);
  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState("");
  const [question, setQuestion] = useState("");
  const [timer, setTimer] = useState(20);
  const [token, settoken] = useState(false);
  const [keyword, setKeyword] = useState([]);
  const { Name, SSID, PostId, BirdDay, time } = useSelector(
    (state) => state.post
  );

  //จัดการชุดคำตอบที่จะส่งไปตอนเสร็จ
  const [questionNo, setQuestionNo] = useState([]);
  const [answerQuestion, setAnswerQuestion] = useState([]);
  const [questionID, setQuestionID] = useState(null);
  const [CheckSuccess, setCheckSuccess] = useState(false);
  const [numberQuestion, setNumberQuestion] = useState(0);
  const number = new Array(numberQuestion);
  //=================================================

  //Function โชว์ Button
  const ShowButton = () => {
    return (
      <ShowButton1>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              nextQuestionHandler(false, "...");
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              nextQuestionHandler(true, "...");
            }}
          >
            ใช่
          </Button>
        </div>
      </ShowButton1>
    );
  };
  //style component ของ Progress-------------
  const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
  const Box = styled.div`
    color: #fff !important;
    background-color: #ec6900 !important;
    height: 5px;
    width: 100%;
    animation: ${rotate} ${timer}s linear;
  `;
  const rotate1 = keyframes`
    from {
      opacity: 0.0;
    }
  
    to {
      opacity: 1.0;
    }
  `;
  const ShowButton1 = styled.div`
    textalign: center;
    width: 100%;
    display: flex;
    flexdirection: row;
    animation: ${rotate1} 2s linear;
  `;

  //-----------------------------------------
  useEffect(() => {
    if (!isLoading) {
      Axios({
        method: "post",
        url: "http://34.87.54.114:8003/api/v1/query/question",
        headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
        data: { patient_no: "3640600077526", form_question_id: 1 },
      })
        .then((res) => {
          setPage(res.data.payload);
          setNumberQuestion(page.length);
          //setisLoading(true);
          settoken(true);
          console.log("Import Data Success!", res);
        })
        .catch((error) => {
          console.log("Not found Token or PatientNo", error.response.status);
          if (error.response.status === 401) {
            Axios({
              method: "GET",
              url: "http://34.87.54.114:8003/api/v1/manage/token",
              headers: {
                Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
              },
            })
              .then((res) => {
                localStorage.setItem("jwt", res.data["accept-token"]);
                console.log("Update Token Success!");
                setisLoading(false);
              })
              .catch((error) => {
                console.log("Error Code", error.response.status);
                Router.push("/page.covid1");
              });
          }
          if (error.response.status === 404) {
            console.log("ไม่มีข้อมูลอยู่ในระบบ");
            Router.push("/page.covid2");
          }
        });
    }
  }, []);

  useEffect(() => {
    if (token) {
      console.log("สถานะการโหลดข้อมูล", token);
      console.log("isLoading =", isLoading);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      console.log("คำถามทั้งหมด", page);
      settoken(false);
      loadQuizData();
    }
    if (currentQuestion > 0) {
      console.log("สถานะของ Token =", token);
      console.log("คำถามข้อปัจจุบัน", page[currentQuestion]);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      if (currentQuestion !== currentQuestion - 1) {
        setQuestion(page[currentQuestion].body);
        setoptions(page[currentQuestion].voice);
        setQuestionID(page[currentQuestion].questions_id);
        setKeyword(page[currentQuestion].keywords);
        setTimer(52);
        console.log("QuestionID", questionID);
      }
    }
    if (!disabled) {
      setdisabled(true);
    }
    //setTimeout(()=>{alert('หมดเวลา');},timer*1000);
  }, [token, currentQuestion]);

  //Function การจัดการคำถาม==============================
  const loadQuizData = () => {
    setdisabled(false);
    setQuestion(page[currentQuestion].body);
    setoptions(page[currentQuestion].voice);
    setQuestionID(page[currentQuestion].questions_id);
    setKeyword(page[currentQuestion].keywords);
    setisLoading(true);
    setShowResults(true);
    console.log("Page have", page);
    console.log("QuestionID", questionID);
  };

  const nextQuestionHandler = async (answer, raw_answer) => {
    if (currentQuestion === page.length - 1) {
      finishHandler(answer, raw_answer);
    } else {
      if (answer === true) {
        setscore(score + 1);
      } else {
        setscore(score);
      }
      setQuestionNo([
        ...questionNo,
        {
          question_id: questionID,
          answer: answer,
          rew_answer: raw_answer,
        },
      ]);
      setCurrentQuestion(currentQuestion + 1);
      console.log("Question", currentQuestion, questionNo);
    }
  };

  const finishHandler = (answer, raw_answer) => {
    setAnswerQuestion([...answerQuestion, answer]);
    setQuestionNo([
      ...questionNo,
      {
        question_id: questionID,
        answer: answer,
        rew_answer: raw_answer,
      },
    ]);
    if (answer === "yes") {
      setscore(score + 1);
    }
    if (currentQuestion === page.length - 1) {
      setisEnd(true);
    }
  };

  const saveDataHandler = async () => {
    setCheckSuccess(true);
    console.log("เตรียม", questionNo);
    await Axios({
      method: "post",
      url: "http://34.87.54.114:8003/api/v1/query/sendQuestion",
      headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
      data: {
        /*patient_no: SSID,
        form_question_id: 1,
        form_question: questionNo*/

        patient_no: 1103702303712,
        form_questions_id: 2,
        form_questions: [
          {
            question_id: 4,
            answer: true,
            rew_answer: "ไม่มีอาการ",
          },
          {
            question_id: 5,
            answer: true,
            rew_answer: "ไม่มีอาการ",
          },
          {
            question_id: 6,
            answer: true,
            rew_answer: "ไม่มีอาการ",
          },
          {
            question_id: 7,
            answer: true,
            rew_answer: "ไม่มีอาการ",
          },
        ],
      },
    })
      .then(function (res) {
        console.log("Success to Save Data!", res);
        console.log("เตรียม", questionNo);
        Router.push("/page.covidcomplete");
      })
      .catch((error) => {
        console.log("Not found Token or PatientNo", error.response.status);
        if (error.response.status === 401) {
          Axios({
            method: "GET",
            url: "http://34.87.54.114:8003/api/v1/manage/token",
            headers: {
              Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
            },
          })
            .then((res) => {
              localStorage.setItem("jwt", res.data["accept-token"]);
              console.log("Update Token Success!");
              setisLoading(false);
            })
            .catch((error) => {
              console.log("Error Code", error.response.status);
              Router.push("/page.covid1");
            });
        }
        if (error.response.status === 404) {
          console.log("ไม่มีข้อมูลอยู่ในระบบ");
          Router.push("/page.covid2");
        }
      });
  };

  //====================================================
  if (isLoading) {
    if (isEnd) {
      if (!CheckSuccess) {
        saveDataHandler();
      }
      return null;
    } else {
      return (
        <FormItem>
          <div className="nu-font-covid">
            <div className="nu-flex-column-fs">
              <div>
                <HeaderCovid link={"/page.covid4"} />
              </div>
              <div className="nu-flex-row-bsauto-h100">
                <div className="nu-fs10-covidqt">
                  <div>
                    <Button shape="circle">{"<"}</Button>
                  </div>
                </div>
                <div className="nu-flex-column-bsauto-w100 nu-center-covidss nu-h100-covidqt">
                  <div className="nu-dp-name-covidqt">
                    <Speechtotext
                      speech={nextQuestionHandler}
                      keyword={keyword}
                      page={"covidquestion"}
                    />
                    <AudioPlayer
                      url={options}
                      token={localStorage.getItem("jwt")}
                      isLoading={isLoading}
                    />
                    <p />
                    <QuestionCovid text={question} check={token} />
                  </div>
                  <div className="nu-center-covidss nu-dp-btn-covidqt">
                    {isLoading ? <ShowButton /> : null}
                  </div>
                </div>
                <div className="nu-fs10-covidqt">
                  <div style={{ textAlign: "right" }}>
                    <Button shape="circle">{">"}</Button>
                  </div>
                </div>
              </div>
              <div className="nu-dp-ud-covidqt">
                <div className="nu-dp-pgnt-covidqt">
                  <div className="nu-pgnt-covidqt">
                    <div className="nu-box-covid1" style={{ marginLeft: "8%" }}>
                      {page.map((page, index) => {
                        if (index === currentQuestion) {
                          return (
                            <span
                              className="nu-box1-covid1"
                              style={{ background: "white" }}
                            />
                          );
                        }
                        return <span className="nu-box1-covid1" />;
                      })}
                    </div>
                  </div>
                </div>
                <div>
                  <Button
                    shape="circle"
                    size="middle"
                    className="nu-btn-reset-covidqt"
                  >
                    <img src={img1} style={{ width: "auto", height: "auto" }} />
                  </Button>
                </div>
              </div>
              <div className="nu-dptm-covidqt">
                <h5 className="nu-h5-covidqt">
                  <Duration seconds={time} />
                </h5>
              </div>
              <div>
                <div className="w3-light-grey">
                  <Box />
                </div>
              </div>
              <div className="nu-dp-wave-covidqt">
                <div className="sound-icon disabled">
                  <div className="sound-wave">
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                  </div>
                </div>
              </div>
              <div className="nu-flex-btn-covid1">
                <h5 style={{ color: "#FFFFFF80" }}>
                  Copyright © All rights reserved. 2020 มหาวิทยาลัยนเรศวร
                  Naresuan University
                </h5>
              </div>
            </div>
          </div>
        </FormItem>
      );
    }
  } else {
    return null;
  }
}

export default CovidQuestion;
