import React, { useMemo, useState } from "react";
import { Button } from "antd";

function ButtonQuestion(props) {
  const [check, setCheck] = useState(false);

  useMemo(() => {
    const A = () => {
      return new Promise(async (resolve) => {
        await setCheck(false);
        await setCheck(true);
      });
    };
    A();
    console.log("button");
    //setCheck(true);
  }, [props.question]);

  if (check) {
    return (
      <div className="nu-btn-showbtn">
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              props.click(false, "...");
              //setCheck(false);
              console.log("click");
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              props.click(true, "...");
              //setCheck(false);
            }}
          >
            ใช่
          </Button>
        </div>
      </div>
    );
  } else {
    return null;
  }
}

export default ButtonQuestion;
