import React, { Component, useState, useEffect } from "react";
import { Form, Button } from "antd";
const FormItem = Form.Item;

//const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
//var SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
//var SpeechRecognition = SpeechRecognition || window.webkitSpeechRecognition
//var recognition = new SpeechRecognition();
//console.log(new SpeechRecognition.SpeechRecognition());
//let recognition = new SpeechRecognition()

const Dictaphone = () => {
  const [Text, SetText] = useState("");
  const [Keyword, SetKeyword] = useState(["ใช่", "ภูเก็ต"]);
  const [isReady, SetisReady] = useState(0);
  const [start, SetStart] = useState(false);
  const [value, SetValue] = useState(0);

  const [Status, SetStatus] = useState(0);
  const [Success, SetSuccess] = useState(0);
  useEffect(() => {
    var SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    var recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "th-TH";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognition.start();
    recognition.onstart = () => {
      console.log("START Recording!", Status);
    };
    const c = () => {
      recognition.onend = (event) => {
        console.log("END Recording!");
        //SetStatus(Status + 1);
      };
    };
    const b = () => {
      recognition.onresult = async (event) => {
        await SetText(event.results[0][0].transcript);
        await SetValue(value + 1);
        await SetStatus(Status + 1);
        await console.log("WaitText!", Text);
      };
    };
    b();
    c();
  }, [isReady]);

  useEffect(() => {
    if (start) {
      if (Text === "") {
        SetisReady(isReady + 1);
      } else {
        
          const word = Keyword;
          const sentence = Text;
          var n = 0;
          const a = [];
          function Check() {
            const result = word.map((word, n) => {
              console.log('1')
              if (sentence.includes(word)) {
                console.log(sentence);
                n = n + 1;
                //SetSuccess(Success + 1);
                return "มีคำว่า : " + word + " : ครั้งที่ : " + value;
              } else {
                console.log(sentence);
                return "ไม่มีคำว่า : " + word + " : ครั้งที่ : " + value;
              }
            });
          }
          Check();
          console.log(result);
          console.log('2');
        

        /*if (n >= 0) {
          a.push(result);
          console.log(a);
        } else {
          SetisReady(isReady + 1);
          console.log(a);
        }*/
        //console.log(result);
        //console.log(isReady);
      }
    } else {
      SetStart(true);
    }
  }, [Status]);

  return (
    <div>
      <FormItem>
        <button>Start</button>
        <h1>{Text}</h1>
      </FormItem>
    </div>
  );
};

export default Dictaphone;
