import React, { useEffect, useState } from "react";
import Webcam from "react-webcam";
import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
} from "antd";
import { useDispatch, useSelector } from "react-redux";
import {
  updateFirstName,
  updateLastName,
  updateSsid,
} from "../../store/actions/postAction";
import img1 from "../../public/1.png";
import Router from "next/router";
import Axios from "axios";

const FormItem = Form.Item;

const WebcamCapture = (props) => {
  const dispatch = useDispatch();
  const webcamRef = React.useRef(null);
  const [data, setData] = useState("");
  const [imgSrc, setImgSrc] = React.useState(null);
  const { SSID } = useSelector((state) => state.post);
  const token =
    "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9..SpszxcoVLCt49PKRXq_d9fVL2xT6VMyQwka54554SgZYJzAPDc1REOi_fgj1SppKckXBE5pE63SlHkP1tkifkgUw9FwQiRFEysds0_u4EhRteu4IBy-QMCtJPjieWYQ38HKDns8baLKVBE-kuOhQ9wKEzt4mGDwF-fYHcZ9kzFImkNSHHft_y9HQIb-sjUCNzp6G59YHm75yG1Pqf3rIAeuHk-Vgol_5bX6ANpGGOLJR_vMS9Csk18I9rrRVdZVne45JcEEJBLitVglkU74zxkECXev4yVKCuOPfclPnCuLCal16sUHaA9XNrCYog24fB5CfTOV8Qz3grnH36kKw71sbtUjygJ7Zp4e4f8fJnyjEpP_FJLpi6_VTo-HLqQvi-x6hzX5SD-JjVOOSezJxeDoL7Jeu-iPRqZRVm7M646bcNxXsC18_2JF3AJ9feyqJsAOCrnh1HniunxS8lyIejqwM9162uuyuQmfylu1XMtokClPUbUE1rOJLvFa5vGKyCe_imMC7wA8u4r2ys42grspFrBAXHqXz8isPUbMQUfviev-CprvR8GRNEOfri2wLx0V436Eaai5819VVk8uMAxLqK17Tq5Az9JEyEzmkX4PkZX2REZpaYmYcVhF29--B2Uu9lkkeTnWDre4ZmSf7ApZ65Bv570j-7ozkfS2ogIs";

  const Axiosphoto = (value) => {
    const Command = value;
    console.log(Command);
    if (Command === "Errormask") {
      alert("กรุณาถอดหน้ากาก");
    } else if (Command === "FoundData") {
      const Data = ["3640600077526", "สมชาย", "หมายปอง"];
      dispatch(updateSsid(Data[0]));
      dispatch(updateFirstName(Data[1]));
      dispatch(updateSsid(Data[2]));
      alert("พบข้อมูล");
      Router.push("/page.covid4");
    } else if (Command === "NotFoundData") {
      alert("ไม่พบข้อมูล");
    }
  };

  function taskOne() {
    return new Promise(async (resolve) => {
      const A = async () => {
        const imageSrc = await webcamRef.current.getScreenshot();
        console.log(imageSrc)
        const blob = await fetch(img1)
          .then((res) => {
            res.blob();
            setData(imageSrc);
            console.log("success 1");
            resolve(res);
          })
          .catch(() => console.log("error"));
      };
      await A();
    });
  }
  function taskTwo(value) {
    return new Promise(async (resolve) => {
      const B = async () => {
        await console.log("A", value);
        await console.log("2");
        const formData = await new FormData();
        await formData.append("picture", img1);
        return formData;
      };
      var formData = await B();
      await console.log("3", formData);
      await Axios({
        method: "post",
        url: "http://34.87.54.114:8003/api/v1/file/uploadphoto",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
        formData,
      })
        .then((res) => {
          console.log("success", res);
        })
        .catch((error) => {
          console.log("error", error);
        });
    });
  }
  function taskThree() {
    return new Promise(async (resolve) => {
      const C = () => {
        console.log("4");
      };
      await C();
      await resolve();
    });
  }

  const main = async () => {
    var result = await taskOne();
    //await taskTwo(result);
    //await taskThree();
  };

  const capture = React.useCallback(async () => {
    //const imageSrc = await webcamRef.current.getScreenshot();
    main();
    //const formData = new FormData();
    //formData.append('picture');
    //console.log(formData)
    /*Axios({
        method:"post",
        url:"http://34.87.54.114:8003/api/v1/file/uploadphoto",
        headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          },
      data:{ picture:blob}
    }).then((res)=>{
        console.log('success',res)
    }).catch((error)=>{
        console.log('error',error)
    })*/
    //await console.log("1", data);
    //await Axiosphoto("Errormask");
  }, [webcamRef, setImgSrc]);
  /*useEffect(() => {
    const formData = new FormData();
    if (data === "") {
    } else {
      console.log(data);
      formData.append("picture.png", data);
      console.log(formData);
    }
  }, [data]);*/
  const [statee, setStatee] = useState(null);
  const fileSelectedHandler = (event) => {
    setStatee(event.target.files[0]);
    console.log(event.target.files[0]);
  };
  const fileUploadHandler = () => {
    console.log("TEST", statee);
    const fd = new FormData();
    fd.append("picture", statee);
    Axios.post("http://34.87.54.114:8003/api/v1/file/uploadphoto", fd, {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((res) => {
        console.log("success", res);
      })
      .catch((err) => {
        console.log("err", err);
      });
  };

  const test = () => {
    function b64toBlob(b64Data, contentType, sliceSize) {
      contentType = contentType || "";
      sliceSize = sliceSize || 512;

      var byteCharacters = atob(b64Data);
      var byteArrays = [];

      for (
        var offset = 0;
        offset < byteCharacters.length;
        offset += sliceSize
      ) {
        var slice = byteCharacters.slice(offset, offset + sliceSize);

        var byteNumbers = new Array(slice.length);
        for (var i = 0; i < slice.length; i++) {
          byteNumbers[i] = slice.charCodeAt(i);
        }

        var byteArray = new Uint8Array(byteNumbers);

        byteArrays.push(byteArray);
      }

      var blob = new Blob(byteArrays, { type: contentType });
      return blob;
    }
    var ImageURL = data;
    console.log(ImageURL)
    var block = ImageURL.split(";");
    var contentType = block[0].split(":")[1];
    var realData = block[1].split(",")[1];
    var blob = b64toBlob(realData, contentType);
    console.log(blob);
    const fd = new FormData();
    fd.append("picture", blob);
    Axios.post("http://34.87.54.114:8003/api/v1/file/uploadphoto", fd, {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((res) => {
        console.log("success", res);
      })
      .catch((err) => {
        console.log("err", err);
      });
  };
  return (
    <div>
      <FormItem>
        <div>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            style={{ borderRadius: "20px" }}
          />
        </div>
        <Button onClick={capture} style={{ width: "100%" }} type="primary">
          Capture photo
        </Button>
        <div>
          <img src={imgSrc} />
          <input type="file" onChange={fileSelectedHandler} />
          <button onClick={fileUploadHandler}>Upload</button>
          <button onClick={test}>Upload TEST</button>
        </div>
      </FormItem>
    </div>
  );
};

export default WebcamCapture;
