  const Axiosphoto = (value) => {
    const Command = value;
    console.log(Command);
    if (Command === "Errormask") {
      alert("กรุณาถอดหน้ากาก");
    } else if (Command === "FoundData") {
      const Data = ["3640600077526", "สมชาย", "หมายปอง"];
      dispatch(updateSsid(Data[0]));
      dispatch(updateFirstName(Data[1]));
      dispatch(updateSsid(Data[2]));
      alert("พบข้อมูล");
      Router.push("/page.covid4");
    } else if (Command === "NotFoundData") {
      alert("ไม่พบข้อมูล");
    }
  };

  const postdata = () => {
    return new Promise(function (resolve, reject) {
        a = ''
        const blob = fetch(img1)
          .then((res) => {
            res.blob();
            setData(res);
            a = res
            console.log("success")
          })
          .catch(() => console.log("error"));
          resolve(console.log("blob",a))
    });

    //const formData = new FormData();
  };

  const postimage = () => {
    return new Promise(function (resolve, reject) {
        /*await Axios({
            method:"post",
            url:"http://34.87.54.114:8003/api/v1/file/uploadphoto",
            headers: {
                Authorization: `Bearer ${token}`,
                'Content-Type': 'multipart/form-data'
              },
          data:{ picture:blob}
        }).then((res)=>{
            console.log('success',res)
        }).catch((error)=>{
            console.log('error',error)
        })*/
        //resolve(console.log("blob"))
    });

    //const formData = new FormData();
  };
  const A = async () => {
    await postdata();
    await postimage();
  }
  const capture = React.useCallback(async () => {
    const imageSrc = await webcamRef.current.getScreenshot();
    //console.log(imageSrc)
    await setImgSrc(imageSrc);
    A()
    //formData.append('picture');
    //console.log(formData)
    /*Axios({
        method:"post",
        url:"http://34.87.54.114:8003/api/v1/file/uploadphoto",
        headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          },
      data:{ picture:blob}
    }).then((res)=>{
        console.log('success',res)
    }).catch((error)=>{
        console.log('error',error)
    })*/
    //await console.log("1", data);
    //await Axiosphoto("Errormask");
