import React, { Component, useContext, useEffect, useState } from "react";
import ReactPlayer from "react-player";
import Axios from "axios";
import { Form, Button } from "antd";
import Duration from "./component.duration";
import { useDispatch, useSelector } from "react-redux";
//import { CounterContext } from "../../store/CouterProvider";
import { updateTime } from "../../store/actions/postAction";
import img1 from "../../public/9.png";

function Player(props) {
  const dispatch = useDispatch();
  const { time } = useSelector((state) => state.post);
  const [url, seturl] = useState(null);
  //const [token, setToken] = useState(localStorage.getItem("jwt"));
  const [duration, setduration] = useState(0);
  const [playing, setplaying] = useState(false);
  const [pip, setpip] = useState(false);
  const [loaded, setloaded] = useState(0);

  useEffect(() => {
    seturl(props.url);
    setpip(true);
    setplaying(true);
  }, [props.url]);

  const handleDuration = (duration) => {
    //setduration(duration);
    dispatch(updateTime(duration));
    console.log("onDuration", duration);
    console.log("Time", time);
  };
  const handlePlayPause = () => {
    setplaying(false);
    setplaying(true);
    console.log('button')
  }
  /*const handleSeekChange = (e) => {
    setplayed(parseFloat(e.target.value));
  };*/

  /*const handleProgress = (state) => {
    setA(duration * state.played);
    setB(duration * (1 - state.played));
    //updateDuration((A / duration) * 100);
  };*/
  if (props.isLoading) {
    if (pip) {
      return (
        <div style={{ color: "white" }}>
          <ReactPlayer
            controls={true}
            light={false}
            playing={playing}
            width="0%"
            height="0%"
            url={url}
            onDuration={handleDuration}
            //onProgress={handleProgress}
          />
          <Button onClick={handlePlayPause}  shape="circle" size="middle" className="nu-btn-reset-covidqt">
            <img src={img1} style={{ width: "auto", height: "auto" }} />
          </Button>
        </div>
      );
    } else {
      return null;
    }
  } else {
    return null;
  }
}
export default Player;
