import {
  Form,
  Button,
} from "antd";
import Link from "next/link";
import img1 from "../public/1.png";
import backcovid from "../public/backcovid.png";
import { CaretRightFilled } from "@ant-design/icons";
import { useState,useEffect } from "react";
import { useDispatch } from 'react-redux'
import Axios from "axios";
import {
  updateToken
} from "../store/actions/postAction";
import Speechtotext from './components/component.speechtotext'
const FormItem = Form.Item;

function CovidPage1() {
  const dispatch = useDispatch();
  const [statusToken, setStatusToken] = useState(false);
  const [token, setToken] = useState(null);


  useEffect(() => {
    localStorage.removeItem('jwt');
    localStorage.removeItem('jwt-refresh');
    localStorage.removeItem('User')
    Axios.get("http://34.87.54.114:8003/api/v1/auth/login", {
      auth: {
        username: "kkk",
        password: "kkk",
      },
    })
      .then((data) => {
        console.log("Success give token");
        localStorage.setItem("jwt", data.data["accept-token"]);
        localStorage.setItem("jwt-refresh", data.data["refresh-token"]);
        setToken(data.data["accept-token"]);
        setStatusToken(data.data.result);
        dispatch(updateToken(data.data["accept-token"]))
      })
      .catch((error) => {
        console.log("Error", error.response.status);
      });
  }, []);



if (statusToken) {
  const keyword = ["เริ่มประเมิน"]
  console.log("Success to find token.");
  return (
  <FormItem>
    <div style={{ marginTop: 0 }}>
        <div
          className="nu-font-covid"
          style={{
            backgroundImage: `url(${backcovid})`,
            backgroundRepeat: "no-repeat",
            backgroundAttachment: "fixed",
            backgroundSize: "cover",
          }}
        >
          <div
            style={{
              background:
                "transparent linear-gradient(180deg, #20202000 0%, #202020 100%) 0% 0% no-repeat padding-box",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                height: "100vh",
                width: "100vw",
              }}
            >
              <div
                style={{
                  display: "flex",
                  flexBasis: "auto",
                  height: "100%",
                  flexDirection: "row",
                }}
              ></div>
              <div
                style={{
                  display: "flex",
                  flexBasis: "auto",
                  flexDirection: "row",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    flexBasis: "50%",
                    width: "100%",
                    justifyContent: "flex-start",
                    alignItems: "center",
                    flexDirection: "column",
                    paddingTop: "10px",
                  }}
                >
                  <div
                    style={{
                      marginBottom: "10px",
                      width: "100%",
                      display: "flex",
                      justifyContent: "center",
                    }}
                  >
                    <Speechtotext speech={'/page.takephoto'} keyword={keyword} page={"covid1"} />
                    <Link href="/page.takephoto">
                      <Button
                        type="primary"
                        size="large"
                        style={{
                          borderRadius: "34px",
                          fontSize: "28px",
                          display: "flex",
                          alignItems: "center",
                        }}
                        htmlType="submit"
                      >
                        <div
                          style={{
                            display: "block",
                            width: "100%",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                          }}
                        >
                          <CaretRightFilled style={{ fontSize: "50px" }} />
                          เริ่มประเมิน
                        </div>
                      </Button>
                    </Link>
                  </div>
                  <div
                    style={{
                      width: "100%",
                      display: "flex",
                      justifyContent: "center",
                    }}
                  >
                    <Button
                      size="large"
                      style={{ borderRadius: "31px", fontSize: "28px" }}
                      htmlType="submit"
                    >
                      รายละเอียด
                    </Button>
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    flexBasis: "auto",
                    width: "100%",
                    flexDirection: "column",
                  }}
                >
                  <h1 style={{ fontSize: "42px", marginBottom: "0px" }}>
                    แบบประเมินโรคไวรัส COVID-19
                  </h1>
                  <h3 style={{ fontSize: "18px" }}>
                    แบบประเมินสงสัยโรคติดเชื้อไวรัสโคโรนาสายพันธุ์ใหม่ 2019
                    (COVIDE 19)
                  </h3>
                  <h4 style={{ fontSize: "18px" }}>
                    22 สิงหาคม 2563 โดยโรงพยาบาลมหาวิทยาลัยนเรศวร{" "}
                    <img src={img1} />
                  </h4>
                </div>
              </div>
              <div
                style={{
                  display: "flex",
                  flexBasis: "auto",
                  flexDirection: "row",
                  justifyContent: "center",
                }}
              >
                <div className="nu-box-covid1" style={{ margin: "10px 0" }}>
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                  <span className="nu-box1-covid1" />
                </div>
              </div>
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  flexDirection: "row",
                  width: "100%",
                }}
              >
                <div
                  style={{
                    flexBasis: "auto",
                    width: "100%",
                    display: "flex",
                    justifyContent: "center",
                    marginLeft: "4%",
                    alignItems: "center",
                  }}
                >
                  <h5 style={{ color: "#FFFFFF80" }}>
                    Copyright © All rights reserved. 2020 มหาวิทยาลัยนเรศวร
                    Naresuan University
                  </h5>
                </div>
                <div style={{ flexBasis: "auto", margin: "5px" }}>
                  {/*<Button shape="circle"><img src={img2} /></Button>*/}
                </div>
              </div>
            </div>
          </div>
        </div>
    </div>
    </FormItem>
  );
} else {
  console.log("False to find token.");
  return null;
}
}

export default CovidPage1;
