import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
  Input,
} from "antd";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from '../public/9.png'

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [showResults, setShowResults] = useState(false);
  //State จัดการ เรื่องเวลาของเสียง
  //const { Timeduration } = useContext(CounterContext);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);
  const [disabled, setdisabled] = useState(true);
  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [token, settoken] = useState(false);
  const { Name, SSID, PostId, BirdDay } = useSelector((state) => state.post);
  //=================================================

  //Function โชว์ Button
  const ShowButton = () => {
    return (
      <div
        style={{
          textAlign: "center",
          width: "100%",
          display: "flex",
          flexDirection: "row",
        }}
      >
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              nextQuestionHandler("yes");
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              nextQuestionHandler("no");
            }}
          >
            ใช่
          </Button>
        </div>
      </div>
    );
  };
  //style component ของ Progress-------------
  const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
  const Box = styled.div`
    color: #fff !important;
    background-color: #ec6900 !important;
    height: 5px;
    width: 100%;
    animation: ${rotate} 52s linear;
  `;
  //-----------------------------------------

  useEffect((prevState) => {
    if (!isLoading) {
      fetch("https://smxv3.sse.codesandbox.io/question")
        .then((response) => response.json())
        .then((pages) => {
          setPage(pages);
          setisLoading(true);
          settoken(true);
        });
    }
    if (token) {
      console.log("BAS", token);
      console.log("คำถามที่เท่าไร", currentQuestion);
      console.log("data", page);
      settoken(false);
      loadQuizData();
    }
    if (currentQuestion > 0) {
      if (currentQuestion !== currentQuestion - 1) {
        setQuestion(page[currentQuestion].text);
        setAnswer(page[currentQuestion].answer);
        setoptions(page[currentQuestion].keyword);
      }
    }
    if (!disabled) {
      setdisabled(true);
    }
  });
  //

  //Function การจัดการคำถาม==============================
  const loadQuizData = () => {
    setdisabled(false);
    setQuestion(page[currentQuestion].text);
    setAnswer(page[currentQuestion].answer);
    setoptions(page[currentQuestion].keyword);
    setShowResults(true);
    console.log("คำถามปัจจุบัน", question, answer, options);
  };
  const nextQuestionHandler = (answer) => {
    if (currentQuestion === page.length - 1) {
      finishHandler(answer);
    } else {
      if (answer === "yes") {
        setscore(score + 1);
      } else {
        setscore(score);
      }
      setCurrentQuestion(currentQuestion + 1);
      console.log("Question", currentQuestion, score);
    }
  };

  const finishHandler = (answer) => {
    if (currentQuestion === page.length - 1) {
      setisEnd(true);
    }
    if (answer === "yes") {
      setscore(score + 1);
    }
  };
  //====================================================
  if (isEnd) {
    console.log("Profile", [Name, SSID, BirdDay, PostId]);
    Router.push("/page.covidcomplete");
    return null;
  } else {
    return (
      <FormItem>
        <div className="nu-font-covid">
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              width: "100vw",
              height: "100vh",
            }}
          >
            <div
              style={{
                flexBasis: "100px",
                height: "100%",
              }}
            >
              <HeaderCovid />
            </div>

            <div
              style={{
                flexBasis: "auto",
                height: "100%",
                display: "flex",
                flexDirection: "row",
              }}
            >
              <div style={{ flexBasis: "10%", width: "100%", margin: "auto" }}>
                <div>
                  <Button shape="circle">{"<"}</Button>
                </div>
              </div>
              <div
                style={{
                  background: "",
                  flexBasis: "auto",
                  width: "100%",
                  height: "100%",
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  flexDirection: "column",
                }}
              >
                <div
                  style={{
                    flexBasis: "auto",
                    display: "flex",
                    width: "100%",
                    height: "100%",
                    alignItems: "center",
                    flexDirection: "column",
                    padding: "5%",
                    justifyContent: "center",
                  }}
                >
                  <div
                    style={{
                      width: "100%",
                      height: "100%",
                      display: "flex",
                      alignItems: "flex-end",
                      justifyContent: "center",
                      padding: "0px 20%",
                    }}
                  >
                    {/*<AudioPlayer url="" />
                  <p />*/}
                    <QuestionCovid text={question} check={token} />
                  </div>
                  <div
                    style={{
                      width: "100%",
                      height: "100%",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    {showResults ? <ShowButton /> : null}
                  </div>
                </div>
              </div>
              <div style={{ flexBasis: "10%", width: "100%", margin: "auto" }}>
                <div style={{ textAlign: "right" }}>
                  <Button shape="circle">{">"}</Button>
                </div>
              </div>
            </div>
            <div
              style={{
                flexBasis: "auto",
                display: "flex",
                width: "100%",
                height: "auto",
                alignItems: "center",
                justifyContent: "center",
                flexDirection:"row"
              }}
            >
              <div style={{ width: "100%", flexBasis: "auto" }}>
                <div style={{width:"auto",display:"flex",justifyContent:"center"}}>
                  <box className="nu-box-covid1" style={{marginLeft:"2%"}}>
                  {page.map((page, index) => {
                    if (index === currentQuestion) {
                      return (
                        <span
                          className="nu-box1-covid1"
                          style={{ background: "white" }}
                        />
                      );
                    }
                    return <span className="nu-box1-covid1" />;
                  })}
                </box>
                </div>
                
              </div>
              <div
                style={{
                  flexBasis: "auto",
                }}
              >
                <Button shape="circle" size="middle" style={{width:"53px",height:"53px"}}><img src={img1} style={{width:"auto",height:"auto"}} /></Button>
              </div>
            </div>
            <div
              style={{
                flexBasis: "auto",
                display: "flex",
                justifyContent: "flex-end",
              }}
            >
              <h5
                style={{
                  fontSize: "10px",
                  marginRight: "9px",
                  color: "#FFFFFF80",
                  marginBottom: "2px",
                }}
              >
                3:20
              </h5>
            </div>
            <div
              style={{
                flexBasis: "180px",
                height: "100%",
              }}
            >
              <div className="w3-light-grey">
                <Box />
              </div>
            </div>
            <div
              style={{
                flexBasis: "auto",
                display: "flex",
                justifyContent: "center",
                flexDirection: "row",
                width: "100%",
              }}
            >
              <h5>
                Copyright © All rights reserved. 2020 มหาวิทยาลัยนเรศวร Naresuan
                University
              </h5>
            </div>
