import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
  Input,
} from "antd";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from "../public/9.png";
import Axios from "axios";
import AudioPlayer from "./components/component.audioplayer";

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [showResults, setShowResults] = useState(false);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);
  const [disabled, setdisabled] = useState(true);
  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [token, settoken] = useState(false);
  const { Name, SSID, PostId, BirdDay, time } = useSelector(
    (state) => state.post
  );

  //จัดการชุดคำตอบที่จะส่งไปตอนเสร็จ
  const [questionNo, setQuestionNo] = useState([]);
  const [answerQuestion, setAnswerQuestion] = useState([]);
  const [rawAnswer, setRawAnswer] = useState([]);
  const [tokenNumber, settokenNumber] = useState(false);
  const [numberQuestion, setNumberQuestion] = useState(0);
  const number = new Array(numberQuestion);
  //=================================================

  //Function โชว์ Button
  const ShowButton = () => {
    return (
      <div
        style={{
          textAlign: "center",
          width: "100%",
          display: "flex",
          flexDirection: "row",
        }}
      >
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              nextQuestionHandler("no");
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              nextQuestionHandler("yes");
            }}
          >
            ใช่
          </Button>
        </div>
      </div>
    );
  };
  //style component ของ Progress-------------
  const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
  const Box = styled.div`
    color: #fff !important;
    background-color: #ec6900 !important;
    height: 5px;
    width: 100%;
    animation: ${rotate} 15s linear;
  `;
  //-----------------------------------------

  useEffect(() => {
    console.log('isLoading 1',isLoading)
    if (!isLoading) {
       Axios({
        method: "post",
        url: "http://34.87.54.114:8003/api/v1/query/question",
        headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
        data: { patient_no: "3640600077526", form_question_id: 1 },
      })
        .then((res) => {
          console.log("Import Data Success!",res);
          console.log('isLoading 5',isLoading)
          setPage(res.data.payload);
          if (!tokenNumber) {
            setNumberQuestion(page.length);
            settokenNumber(true);
            console.log('isLoading 2',isLoading)
            setisLoading(true);
            settoken(true);
            console.log('ตำแหน่ง 1')
            console.log('isLoading 3',isLoading)
          }
        })
        .catch((error) => {
          console.log("Not found Token or PatientNo",error.response.status);
          if (error.response.status === 401) {
            Axios({
              method: "GET",
              url: "http://34.87.54.114:8003/api/v1/manage/token",
              headers: {
                Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
              },
            })
              .then((res) => {
                localStorage.setItem("jwt", res.data["accept-token"]);
                console.log("Update Token Success!");
                setisLoading(false);
              })
              .catch((error) => {
                console.log("Error Code", error.response.status);
                Router.push("/page.covid1");
              });
          }
          if (error.response.status === 404) {
            console.log("ไม่มีข้อมูลอยู่ในระบบ");
            Router.push("/page.covid2");
          }
        });
    }
    if (token) {
      console.log("สถานะการโหลดข้อมูล", token);
      console.log('isLoading 4',isLoading)
      console.log("คำถามที่เท่าไร", currentQuestion);
      console.log("คำถามทั้งหมด", page);
       settoken(false);
       loadQuizData();
    }
    if (currentQuestion > 0) {
      console.log("BAS", token);
      console.log("คำถาม", page[currentQuestion]);
      if (currentQuestion !== currentQuestion - 1) {
        setQuestion(page[currentQuestion].body);
        setoptions(page[currentQuestion].voice);
      }
    }
    if (!disabled) {
      setdisabled(true);
    }
  });

  //Function การจัดการคำถาม==============================
  const loadQuizData = () => {
    setdisabled(false);
    setQuestion(page[currentQuestion].body);
    setoptions(page[currentQuestion].voice);
    setShowResults(true);
  };

  const nextQuestionHandler = async (answer) => {
    if (currentQuestion === page.length - 1) {
      finishHandler(answer);
    } else {
      if (answer === "yes") {
        setscore(score + 1);
        setAnswerQuestion([...answerQuestion, answer]);
      } else {
        setscore(score);
        setAnswerQuestion([...answerQuestion, answer]);
      }
      setQuestionNo([
        ...questionNo,
        {
          questionNo: currentQuestion + 1,
          answerQuestion: answer,
          rawAnswer: "...",
        },
      ]);
      //setRawAnswer([...rawAnswer, {rawAnswer:}])
      setCurrentQuestion(currentQuestion + 1);
      console.log("Question", currentQuestion, score, number, answerQuestion);
    }
  };

  const finishHandler = (answer) => {
    if (currentQuestion === page.length - 1) {
      setisEnd(true);
    }
    setAnswerQuestion([...answerQuestion, answer]);
    setQuestionNo([
      ...questionNo,
      {
        questionNo: currentQuestion + 1,
        answerQuestion: answer,
        rawAnswer: "...",
      },
    ]);
    if (answer === "yes") {
      setscore(score + 1);
    }
  };
  const saveDataHandler = async () => {
    await Axios.post(
      "https://smxv3.sse.codesandbox.io/patientScreeningResult",
      {
        patient_no: SSID,
        form_question: questionNo,
        create_by: "robot_001",
      }
    )
      .then(function (res) {
        console.log("sucsess", res);
        Router.push("/page.covidcomplete");
      })
      .catch(function () {
        console.log("error");
      });
  };
  //====================================================
  if (isLoading) {
    if (isEnd) {
      console.log("Profile", [Name, SSID, BirdDay, PostId]);
      console.log(
        "Question",
        currentQuestion,
        score,
        number,
        answerQuestion,
        questionNo
      );
      //Router.push("/page.covidcomplete");
      saveDataHandler();
      return null;
    } else {
      return (
        <FormItem>
          <div className="nu-font-covid">
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                width: "100vw",
                height: "100vh",
              }}
            >
              <div
                style={{
                  flexBasis: "100px",
                  height: "100%",
                }}
              >
                <HeaderCovid />
              </div>

              <div
                style={{
                  flexBasis: "auto",
                  height: "100%",
                  display: "flex",
                  flexDirection: "row",
                }}
              >
                <div
                  style={{ flexBasis: "10%", width: "100%", margin: "auto" }}
                >
                  <div>
                    <Button shape="circle">{"<"}</Button>
                  </div>
                </div>
                <div
                  style={{
                    background: "",
                    flexBasis: "auto",
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    flexDirection: "column",
                  }}
                >
                  <div
                    style={{
                      flexBasis: "auto",
                      display: "flex",
                      width: "100%",
                      height: "100%",
                      alignItems: "center",
                      flexDirection: "column",
                      padding: "5%",
                      justifyContent: "center",
                    }}
                  >
                    <div
                      style={{
                        width: "100%",
                        height: "100%",
                        display: "flex",
                        alignItems: "flex-end",
                        justifyContent: "center",
                        padding: "0px 20%",
                      }}
                    >
                      {/*<AudioPlayer
                        url={options} /*token={localStorage.getItem('jwt')}
                      />*/}
                      <p />
                      <QuestionCovid text={question} check={token} />
                    </div>
                    <div
                      style={{
                        width: "100%",
                        height: "100%",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                    >
                      {showResults ? <ShowButton /> : null}
                    </div>
                  </div>
                </div>
                <div
                  style={{ flexBasis: "10%", width: "100%", margin: "auto" }}
                >
                  <div style={{ textAlign: "right" }}>
                    <Button shape="circle">{">"}</Button>
                  </div>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  width: "100%",
                  height: "auto",
                  alignItems: "center",
                  justifyContent: "center",
                  flexDirection: "row",
                }}
              >
                <div
                  style={{ width: "100%", flexBasis: "auto", height: "100%" }}
                >
                  <div
                    style={{
                      width: "auto",
                      display: "flex",
                      justifyContent: "center",
                      height: "100%",
                      alignItems: "flex-end",
                    }}
                  >
                    <div className="nu-box-covid1" style={{ marginLeft: "8%" }}>
                      {page.map((page, index) => {
                        if (index === currentQuestion) {
                          return (
                            <span
                              className="nu-box1-covid1"
                              style={{ background: "white" }}
                            />
                          );
                        }
                        return <span className="nu-box1-covid1" />;
                      })}
                    </div>
                  </div>
                </div>
                <div
                  style={{
                    flexBasis: "auto",
                  }}
                >
                  <Button
                    shape="circle"
                    size="middle"
                    style={{
                      width: "53px",
                      height: "53px",
                      marginRight: "25px",
                    }}
                  >
                    <img src={img1} style={{ width: "auto", height: "auto" }} />
                  </Button>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  justifyContent: "flex-end",
                }}
              >
                <h5
                  style={{
                    fontSize: "10px",
                    marginRight: "9px",
                    color: "#FFFFFF80",
                    marginBottom: "2px",
                  }}
                >
                  {time}
                </h5>
              </div>
              <div>
                <div className="w3-light-grey">
                  <Box />
                </div>
              </div>
              <div
                style={{
                  flexBasis: "180px",
                  height: "100%",
                }}
              >
                <div className="sound-icon disabled">
                  <div className="sound-wave">
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                  </div>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  justifyContent: "center",
                  flexDirection: "row",
                  width: "100%",
                }}
              >
                <h5 style={{ color: "#FFFFFF80" }}>
                  Copyright © All rights reserved. 2020 มหาวิทยาลัยนเรศวร
                  Naresuan University
                </h5>
              </div>
            </div>
          </div>
        </FormItem>
      );
    }
  } else {
    return null;
  }
}

export default CovidQuestion;
