import React, { useEffect, useState } from "react";
import Webcam from "react-webcam";
import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
} from "antd";
import { useDispatch, useSelector } from "react-redux";
import {
  updateFirstName,
  updateLastName,
  updateSsid,
} from "../../store/actions/postAction";
import img1 from '../../public/1.png'
import Router from "next/router";
import Axios from "axios";

const FormItem = Form.Item;

const WebcamCapture = (props) => {
  const dispatch = useDispatch();
  const webcamRef = React.useRef(null);
  const [data , setData] = useState(null)
  const [imgSrc, setImgSrc] = React.useState(null);
  const { SSID } = useSelector((state) => state.post);
  const token = "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9..ulvQOR5RwRo8aiWllefolWtTEXm0kbBytRg7VFvTYiM6DEWN_pRb8_05efaDAolKKgQE3LRQzpSiQkJhkOaY0TqYbXC1RjAERcFvHEFg33Zf2oMLv_jI-gqGjxfk4BRb6drWke382cEfoONuXZGRzagS1abvAZ8coh4yQMZPdxeI8KO-IDioT0Jp0PY1PL-lMGA4rCK0rONJUjh7QSrej85fGlOeRRxypxYbcpJatpNUw8UY1Ei57w571pUR9p6gXhfHCtuftjZVCSCvJMyBSbKytneK-Dz5cfvh85X_NpjNpy86ahWIzXoNZlLzFb-TGKSWU_mIU6C0xByB0VXNtrBdxjFGaZZferrqJFQe_DJEXLNg44qOrSHRlNHycZt-gdu5RAeX4cI4dPfwKmgy2ahi8b5eaZqHiEuTYvDfmrDdz7R3p9QaahyGX7YC6GPXfE5avE6lONIU0LI65WVXyoo-561gAZEfQKqIs1qjvgDqiCw56ZGRO4EwFJRna9uz11-7wzimhlXjPKYit8l0JFILfT_4S7X_EOFo1wALrrsimx3X963oPTVvs7hVx02KAhQWd6-YQSBVrykpusK5ZOVPCa8gibSUZaAGuRrWBnOB0otc2Fk75vMbIjJ2P-mWReLkZ-UOo5ea_NA86yDouXNUzpbtkCZT7mRZWTVDZvk"

  const Axiosphoto = (value) => {
    const Command = value;
    console.log(Command);
    if (Command === "Errormask") {
      alert("กรุณาถอดหน้ากาก");
    } else if (Command === "FoundData") {
      const Data = ["3640600077526","สมชาย","หมายปอง"];
      dispatch(updateSsid(Data[0]));
      dispatch(updateFirstName(Data[1]));
      dispatch(updateSsid(Data[2]));
      alert("พบข้อมูล");
      Router.push("/page.covid4");
    } else if (Command === "NotFoundData") {
      alert("ไม่พบข้อมูล");
    }
  };
  const capture = React.useCallback( async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    //console.log(imageSrc)
    await setImgSrc(imageSrc);
    const blob = await fetch(img1).then(res => {
        res.blob();console.log('success');setData(res)
    }).catch(()=>console.log('error'));
    await console.log(blob)
    const formData = new FormData();
    //formData.append('picture');
    //console.log(formData)
    Axios({
        method:"post",
        url:"http://34.87.54.114:8003/api/v1/file/uploadphoto",
        headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          },
      data:{ picture:blob}
    }).then((res)=>{
        console.log('success',res)
    }).catch((error)=>{
        console.log('error',error)
    })

    //await Axiosphoto("Errormask");
  }, [webcamRef, setImgSrc]);

  return (
    <div>
      <FormItem>
        <div>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            style={{ borderRadius: "20px" }}
          />
        </div>
        <Button onClick={capture} style={{ width: "100%" }} type="primary">
          Capture photo
        </Button>
        <div><img src={imgSrc} /></div>
      </FormItem>
    </div>
  );
};

export default WebcamCapture;
