import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
  Input,
} from "antd";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from "../public/9.png";
import Axios from "axios";
import AudioPlayer from "./components/component.audioplayer";

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [showResults, setShowResults] = useState(false);
  //State จัดการ เรื่องเวลาของเสียง
  //const { Timeduration } = useContext(CounterContext);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);
  const [disabled, setdisabled] = useState(true);
  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [token, settoken] = useState(false);
  const { Name, SSID, PostId, BirdDay } = useSelector((state) => state.post);

  //จัดการชุดคำตอบที่จะส่งไปตอนเสร็จ
  const [questionNo, setQuestionNo] = useState([]);
