import React, { Component, useState, useEffect } from "react";
import { Form, Button } from "antd";
const FormItem = Form.Item;

const Dictaphone = () => {
  const [Text, SetText] = useState("");
  const [Keyword, SetKeyword] = useState(["ใช่", "ภูเก็ต"]);
  const [isReady, SetisReady] = useState(0);
  const [start, SetStart] = useState(false);
  const [value, SetValue] = useState(0);
  const [Status, SetStatus] = useState(0);
  const [Success, SetSuccess] = useState(0);
  navigator.mediaDevices.getUserMedia({ audio: true })
      .then(function(stream) {
        console.log('You let me use your mic!')
      })
      .catch(function(err) {
        console.log('No mic for you!')
      });

  useEffect(() => {
    var SpeechRecognition =
      SpeechRecognition || webkitSpeechRecognition;
    var recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "th-TH";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    //recognition.start();
    document.body.onclick = function() {
      recognition.start();
      console.log('Ready to receive a color command.');
    }
    recognition.onstart = () => {
      console.log("START Recording!", Status);
    };
    const c = () => {
      recognition.onend = (event) => {
        console.log("END Recording!");
        SetStatus(Status + 1);
      };
    };
    const b = () => {
      recognition.onresult = (event) => {
        SetText(event.results[0][0].transcript);
        SetValue(value + 1);
        //SetStatus(Status + 1);
        console.log("WaitText!", Text);
      };
    };
    b();
    c();
  }, [isReady]);

  useEffect(() => {
    if (start) {
      if (Text === "") {
        SetisReady(isReady + 1);
      } else {
        const word = Keyword;
        const sentence = Text;
        const a = [];
        var check = false;
        const AA = () => {
          return new Promise(function(resolve,reject) {
            const result = word.map((word, n) => {
              if (sentence.includes(word)) {
                console.log("มีคำว่า : " + word + " : ครั้งที่ : " + value);
                //SetSuccess(Success + 1);
                a.push("มีคำว่า : " + word + " : ครั้งที่ : " + value)
                return "มีคำว่า : " + word + " : ครั้งที่ : " + value;
              } else {
                console.log("ไม่มีคำว่า : " + word + " : ครั้งที่ : " + value);
                return "ไม่มีคำว่า : " + word + " : ครั้งที่ : " + value;
              }
            });
            resolve(console.log('Success'))
          })
        }
        const BB = () => {
          console.log('Succcccccccccccc!!!')
          console.log(a)
          if(a.length >= 1) {
            //SetSuccess(Success + 1);
            console.log('สำเร็จ')
          } else {
            SetisReady(isReady + 1);
          }
        }
        async function Startt() {
          await AA();
          await BB();
        }
        Startt();
      }
    } else {
      SetStart(true);
    }
  }, [Status]);

  return (
    <div>
      <FormItem>
        <button>Start</button>
        <h1>{Text}</h1>
      </FormItem>
    </div>
  );
};

export default Dictaphone;
