import { Form, Button } from "antd";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from "../public/9.png";
import Axios from "axios";
import AudioPlayer from "./components/component.audioplayer";
import Duration from "./components/component.duration";
import Speechtotext from "./components/component.speechtotext";
import Copyright from "./components/component.footercopyright";
import ButtonQuestion from "./components/component.buttonquestion";

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [nextform, setNextform] = useState(0);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);

  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState("");
  const [question, setQuestion] = useState("");
  const [token, settoken] = useState(false);
  const [keyword, setKeyword] = useState([]);
  const { time } = useSelector((state) => state.post);

  //จัดการชุดคำตอบที่จะส่งไปตอนเสร็จ
  const [questionNo, setQuestionNo] = useState([]);
  const [questionID, setQuestionID] = useState(null);
  const [CheckSuccess, setCheckSuccess] = useState(false);
  //=================================================

  //style component ของ Progress-------------
  const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
  const Box = styled.div`
    color: #fff !important;
    background-color: #ec6900 !important;
    height: 5px;
    width: 100%;
    animation: ${rotate} 20s linear;
  `;

  //-----------------------------------------
  useEffect(() => {
    if (!isLoading) {
      Axios({
        method: "post",
        url: "http://34.87.54.114:8003/api/v1/query/question",
        headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
        data: { patient_no: "3640600077526", form_question_id: 1 },
      })
        .then((res) => {
          setPage(res.data.payload);
          setisEnd(false);
          setCheckSuccess(false);
          settoken(true);
          console.log("Import Data Success!", res);
        })
        .catch((error) => {
          console.log("Not found Token or PatientNo", error.response.status);
          if (error.response.status === 401) {
            Axios({
              method: "GET",
              url: "http://34.87.54.114:8003/api/v1/manage/token",
              headers: {
                Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
              },
            })
              .then((res) => {
                localStorage.setItem("jwt", res.data["accept-token"]);
                console.log("Update Token Success!");
              })
              .catch((error) => {
                console.log("Error Code", error.response.status);
                Router.push("/page.covid1");
              });
          }
          if (error.response.status === 404) {
            console.log("ไม่มีข้อมูลอยู่ในระบบ");
            Router.push("/page.covid2");
          }
        });
    }
  }, []);

  useEffect(() => {
    if (token) {
      if (questionNo.length >= page.length) {
        setisEnd(true)
      } else {
        if (currentQuestion >= 0) {
          console.log("Current Save!", page);
          setQuestion(page[currentQuestion].body);
          setoptions(page[currentQuestion].voice);
          setQuestionID(page[currentQuestion].question_id);
          setKeyword(page[currentQuestion].keywords);
          setisLoading(true);
          settoken(false);
        }
      }
    }

    /*if (token) {
      console.log("สถานะการโหลดข้อมูล", token);
      console.log("isLoading =", isLoading);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      console.log("คำถามทั้งหมด", page);
      settoken(false);
      loadQuizData();
    }
    if (currentQuestion > 0) {
      console.log("สถานะของ Token =", token);
      console.log("คำถามข้อปัจจุบัน", page[currentQuestion]);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      if (currentQuestion !== currentQuestion - 1) {
        setQuestion(page[currentQuestion].body);
        setoptions(page[currentQuestion].voice);
        setQuestionID(page[currentQuestion].question_id);
        setKeyword(page[currentQuestion].keywords);
        console.log("QuestionID", page[currentQuestion].questions_id);
      }
    }*/
  }, [token, currentQuestion]);

  //Function การจัดการคำถาม==============================
  const loadQuizData = () => {
    setQuestion(page[currentQuestion].body);
    setoptions(page[currentQuestion].voice);
    setQuestionID(page[currentQuestion].question_id);
    setKeyword(page[currentQuestion].keywords);
    setisLoading(true);
    console.log("Page have", page);
    console.log("QuestionID", questionID);
  };

  const nextQuestionHandler = async (answer, raw_answer) => {
    /*if (currentQuestion === page.length - 1) {
      console.log("คำถามข้อสุดท้าย", questionID);
      finishHandler(answer, raw_answer);
    } else {*/
    setQuestionNo([
      ...questionNo,
      {
        question_id: questionID,
        answer: answer,
        rew_answer: raw_answer,
      },
    ]);
    settoken(true);
    setCurrentQuestion(currentQuestion + 1);
    console.log("Question", currentQuestion, questionNo);
    //}
  };

  const finishHandler = (answer, raw_answer) => {
    setQuestionNo([
      ...questionNo,
      {
        question_id: questionID,
        answer: answer,
        rew_answer: raw_answer,
      },
    ]);
    if (currentQuestion === page.length - 1) {
      setisEnd(true);
    }
  };

  const saveDataHandler = async () => {
    let payload = {
      patient_no: 11123123141231,
      form_questions_id: 1,
      form_questions: questionNo,
    };
    console.log(payload);
    setCheckSuccess(true);
    await Axios({
      method: "post",
      url: "http://34.87.54.114:8003/api/v1/query/sendQuestion",
      headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
      data: payload,
    })
      .then(function (res) {
        console.log("Success to Save Data!", res);
        console.log("เตรียม", questionNo);
        const checknextform = res.data.form_question_name;
        if (checknextform === null) {
          Router.push("/page.covidcomplete");
        } else {
          console.log("restart", res.data.payload[0].form_questions);
          settoken(true);
          setCurrentQuestion(0);
          setisEnd(false);
          setPage(res.data.payload.form_questions);
        }
      })
      .catch((error) => {
        console.log("Not found Token or PatientNo", error);
        if (error.response.status === 401) {
          Axios({
            method: "GET",
            url: "http://34.87.54.114:8003/api/v1/manage/token",
            headers: {
              Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
            },
          })
            .then((res) => {
              localStorage.setItem("jwt", res.data["accept-token"]);
              console.log("Update Token Success!");
              setisLoading(false);
            })
            .catch((error) => {
              console.log("Error Code", error.response.status);
              Router.push("/page.covid1");
            });
        }
        if (error.response.status === 404) {
          console.log("ไม่มีข้อมูลอยู่ในระบบ");
          Router.push("/page.covid2");
        }
      });
  };

  const ButtonNext = (condition) => {
    if ("next" === condition) {
      setCurrentQuestion(currentQuestion + 1);
      settoken(true);
    } else if ("back" === condition) {
      setCurrentQuestion(currentQuestion - 1);
      settoken(true);
    }
  };

  //====================================================
  if (isLoading) {
    if (isEnd) {
      if (!CheckSuccess) {
        saveDataHandler();
      }
      return null;
    } else {
      return (
        <FormItem>
          <div className="nu-font-covid">
            <div className="nu-flex-column-fs">
              <div>
                <HeaderCovid link={"/page.covid4"} />
              </div>
              <div className="nu-flex-row-bsauto-h100">
                <div className="nu-fs10-covidqt nu-flexleft-covid">
                  <Button
                    shape="circle"
                    size="middle"
                    className="nu-btn-left-covidqt"
                    onClick={() => ButtonNext("back")}
                  >
                    <LeftOutlined style={{ fontSize: "35px" }} />
                  </Button>
                </div>
                <div className="nu-flex-column-bsauto-w100 nu-center-covidss nu-h100-covidqt">
                  <div className="nu-dp-name-covidqt">
                    <QuestionCovid text={question} check={token} />
                  </div>
                  <div className="nu-center-covidss nu-dp-btn-covidqt">
                    <ButtonQuestion
                      click={nextQuestionHandler}
                      question={question}
                    />
                  </div>
                </div>
                <div className="nu-fs10-covidqt nu-flexright-covid">
                  <Button
                    shape="circle"
                    size="middle"
                    className="nu-btn-right-covidqt"
                    onClick={() => ButtonNext("next")}
                  >
                    <RightOutlined style={{ fontSize: "35px" }} />
                  </Button>
                </div>
              </div>
              <div className="nu-dp-ud-covidqt">
                <div className="nu-dp-pgnt-covidqt">
                  <div className="nu-pgnt-covidqt">
                    <div
                      className="nu-box-covid1"
                      style={{ marginLeft: "70px" }}
                    >
                      {page.map((page, index) => {
                        if (index === currentQuestion) {
                          return (
                            <span
                              className="nu-box1-covid1"
                              style={{ background: "white" }}
                            />
                          );
                        }
                        return <span className="nu-box1-covid1" />;
                      })}
                    </div>
                  </div>
                </div>
                <div>
                  <AudioPlayer
                    url={options}
                    token={localStorage.getItem("jwt")}
                    isLoading={isLoading}
                  />
                </div>
              </div>
              <div className="nu-dptm-covidqt">
                <h5 className="nu-h5-covidqt">
                  <Duration seconds={time} />
                </h5>
              </div>
              <div>
                <div className="w3-light-grey">
                  <Box />
                </div>
              </div>
              <div className="nu-dp-ud-covidqt" style={{ flexBasis: "180px" }}>
                <div className="nu-dp-pgnt-covidqt">
                  <div
                    className="sound-icon disabled"
                    style={{ marginLeft: "80px" }}
                  >
                    <div className="sound-wave">
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                      <i className="bar"></i>
                    </div>
                  </div>
                </div>
                <div style={{ height: "100%", marginTop: "20px" }}>
                  <Speechtotext
                    speech={nextQuestionHandler}
                    keyword={keyword}
                    page={"covidquestion"}
                  />
                </div>
              </div>
              <div className="nu-flex-btn-covid1">
                <Copyright />
              </div>
            </div>
          </div>
        </FormItem>
      );
    }
  } else {
    return null;
  }
}

export default CovidQuestion;
