import React, { Component, useState, useEffect } from "react";
import { Form, Button } from "antd";
const FormItem = Form.Item;

//const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
//var SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
//var SpeechRecognition = SpeechRecognition || window.webkitSpeechRecognition
//var recognition = new SpeechRecognition();
//console.log(new SpeechRecognition.SpeechRecognition());
//let recognition = new SpeechRecognition()

const Dictaphone = () => {
  const [Text, SetText] = useState("");
  const [Keyword, SetKeyword] = useState(["ใช่,ไปมา"]);
  const [isReady, SetisReady] = useState(0);
  const [start, SetStart] = useState(false);
  const [value, SetValue] = useState(0);

  const [Status, SetStatus] = useState(false);

  useEffect(async () => {
    var SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    var recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "th-TH";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.start();
    recognition.onspeechstart = () => {
      console.log("Speech has been detected");
    };
    recognition.onstart = () => {
      console.log("START Recording!");
    };
    recognition.onaudiostart = () => {
      console.log('Audio capturing started');
    }
    const c = () => {recognition.onend = (event) => {
      console.log("END Recording!");
    };}
    const b = () => {
      recognition.onresult = (event) => {
        //SetText(event.results[0][0].transcript);
        const text = event.results[0][0].transcript;
        SetValue(value + 1);
        console.log("WaitText!", text);
        return text;
      };
    };
    await b();
    await c();
    await console.log("A : ", text);
  }, [isReady]);

  useEffect(() => {
    if (start) {
      if (Text === "") {
        //SetisReady(false);
      } else {
        const word = Keyword;
        const sentence = Text;
        const result = word.map((word) => {
          if (sentence.includes(word)) {
            console.log(sentence);
            return "มีคำว่า : " + word + " : ครั้งที่ : " + value;
          } else {
            console.log(sentence);
            SetisReady(false);
            return "ไม่มีคำว่า : " + word + " : ครั้งที่ : " + value;
          }
        });
        console.log(result);
        console.log(isReady);
      }
    } else {
      SetStart(true);
    }
  }, [Text]);

  return (
    <div>
      <FormItem>
        <button>Start</button>
        <h1>{Text}</h1>
      </FormItem>
    </div>
  );
};

export default Dictaphone;
