import React, { useMemo, useState } from "react";
import { Button } from "antd";

function ButtonQuestion(props) {
  const [check, setCheck] = useState(false);

  useMemo(() => {
    setCheck(true);
  }, [props.question]);

  if (check) {
    return (
      <div className="nu-btn-showbtn">
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              props.click(false, "...");
              setCheck(false);
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              props.click(true, "...");
              setCheck(false);
            }}
          >
            ใช่
          </Button>
        </div>
      </div>
    );
  } else {
    return (
      <div className="nu-btn-showbtn">
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            //style={{opacity:"0"}}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            //style={{opacity:"0"}}
          >
            ใช่
          </Button>
        </div>
      </div>
    );
  }
}

export default ButtonQuestion;
