import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
  Input,
} from "antd";
import Router from "next/router";
import React, { useState, useEffect } from "react";
import HeaderCovid from "./components/component.headercovid";
import QuestionCovid from "./components/component.question";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import img1 from "../public/9.png";
import Axios from "axios";
import AudioPlayer from "./components/component.audioplayer";
import Duration from "./components/component.duration";

const FormItem = Form.Item;
function CovidQuestion(props) {
  //State จัดการ Button
  const [showResults, setShowResults] = useState(false);
  //State จัดการเรื่องชุดคำถาม =========================
  const [page, setPage] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setscore] = useState(0);
  const [disabled, setdisabled] = useState(true);
  const [isEnd, setisEnd] = useState(false);
  const [isLoading, setisLoading] = useState(false);
  const [options, setoptions] = useState("");
  const [question, setQuestion] = useState("");
  const [timer, setTimer] = useState(20);
  const [token, settoken] = useState(false);
  const { Name, SSID, PostId, BirdDay, time } = useSelector(
    (state) => state.post
  );

  //จัดการชุดคำตอบที่จะส่งไปตอนเสร็จ
  const [questionNo, setQuestionNo] = useState([]);
  const [answerQuestion, setAnswerQuestion] = useState([]);
  const [questionID, setQuestionID] = useState(null);
  const [CheckSuccess, setCheckSuccess] = useState(false);
  const [numberQuestion, setNumberQuestion] = useState(0);
  const number = new Array(numberQuestion);
  //=================================================

  //Function โชว์ Button
  const ShowButton = () => {
    return (
      <ShowButton1>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-end",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            onClick={() => {
              nextQuestionHandler(false);
            }}
          >
            ไม่ใช่
          </Button>
        </div>
        <div
          style={{
            width: "100%",
            flexBasis: "auto",
            justifyContent: "flex-start",
            display: "flex",
          }}
        >
          <Button
            className="nu-btn-question"
            type="primary"
            onClick={() => {
              nextQuestionHandler(true);
            }}
          >
            ใช่
          </Button>
        </div>
      </ShowButton1>
    );
  };
  //style component ของ Progress-------------
  const rotate = keyframes`
    from {
      width: 0%;
    }
  
    to {
      width: 100%;
    }
  `;
  const Box = styled.div`
    color: #fff !important;
    background-color: #ec6900 !important;
    height: 5px;
    width: 100%;
    animation: ${rotate} ${timer}s linear;
  `;
  const rotate1 = keyframes`
    from {
      opacity: 0.0;
    }
  
    to {
      opacity: 1.0;
    }
  `;
  const ShowButton1 = styled.div`

    textalign: center;
    width: 100%;
    display: flex;
    flexdirection: row;
    animation: ${rotate1} 1s linear;
  `;
  
  
  //-----------------------------------------
  useEffect(() => {
    if (!isLoading) {
      Axios({
        method: "post",
        url: "http://34.87.54.114:8003/api/v1/query/question",
        headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
        data: { patient_no: "3640600077526", form_question_id: 1 },
      })
        .then((res) => {
          setPage(res.data.payload);
          setNumberQuestion(page.length);
          //setisLoading(true);
          settoken(true);
          console.log("Import Data Success!", res);
        })
        .catch((error) => {
          console.log("Not found Token or PatientNo", error.response.status);
          if (error.response.status === 401) {
            Axios({
              method: "GET",
              url: "http://34.87.54.114:8003/api/v1/manage/token",
              headers: {
                Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
              },
            })
              .then((res) => {
                localStorage.setItem("jwt", res.data["accept-token"]);
                console.log("Update Token Success!");
                setisLoading(false);
              })
              .catch((error) => {
                console.log("Error Code", error.response.status);
                Router.push("/page.covid1");
              });
          }
          if (error.response.status === 404) {
            console.log("ไม่มีข้อมูลอยู่ในระบบ");
            Router.push("/page.covid2");
          }
        });
    }
  }, []);


  useEffect(() => {
    if (token) {
      console.log("สถานะการโหลดข้อมูล", token);
      console.log("isLoading =", isLoading);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      console.log("คำถามทั้งหมด", page);
      settoken(false);
      loadQuizData();
    }
    if (currentQuestion > 0) {
      console.log("สถานะของ Token =", token);
      console.log("คำถามข้อปัจจุบัน", page[currentQuestion]);
      console.log("คำถามที่เท่าไร", currentQuestion + 1);
      if (currentQuestion !== currentQuestion - 1) {
        setQuestion(page[currentQuestion].body);
        setoptions(page[currentQuestion].voice);
        setQuestionID(page[currentQuestion].questions_id);
        setTimer(52);
        console.log('QuestionID',questionID)
      }
    }
    if (!disabled) {
      setdisabled(true);
    }
    setTimeout(()=>{alert('หมดเวลา');},timer*1000);
  },[token,currentQuestion]);

  //Function การจัดการคำถาม==============================
  const loadQuizData = () => {
    setdisabled(false);
    setQuestion(page[currentQuestion].body);
    setoptions(page[currentQuestion].voice);
    setQuestionID(page[currentQuestion].questions_id);
    setShowResults(true);
    setisLoading(true);
    console.log('Page have',page)
    console.log('QuestionID',questionID)
  };

  const nextQuestionHandler = async (answer) => {
    if (currentQuestion === page.length - 1) {
      finishHandler(answer);
    } else {
      if (answer === true) {
        setscore(score + 1);
      } else {
        setscore(score);
      }
      setQuestionNo([
        ...questionNo,
        {
          question_id: questionID,
          answer: answer,
          rew_answer: "...",
        },
      ]);
      setCurrentQuestion(currentQuestion + 1);
      console.log("Question", currentQuestion, questionNo);
    }
  };

  const finishHandler = (answer) => {
    if (currentQuestion === page.length - 1) {
      setisEnd(true);
    }
    setAnswerQuestion([...answerQuestion, answer]);
    setQuestionNo([
      ...questionNo,
      {
        question_id: questionID,
        answer: answer,
        rew_answer: "...",
      },
    ]);
    if (answer === "yes") {
      setscore(score + 1);
    }
  };

  const saveDataHandler = async () => {
    setCheckSuccess(true);
    await Axios({
      method: "post",
      url: "http://34.87.54.114:8003/api/v1/query/sendQuestion",
      headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` },
      data: {
        /*patient_no: SSID,
        form_question_id: 1,
        form_question: questionNo,*/
        
          patient_no:1103702303712,
          form_questions_id:2,
          form_questions:[
              {
                  "question_id":4,
                  "answer":true,
                  "rew_answer":"ไม่มีอาการ"
              },
              {
                  "question_id":5,
                  "answer":true,
                  "rew_answer":"ไม่มีอาการ"
              },
              {
                  "question_id":6,
                  "answer":true,
                  "rew_answer":"ไม่มีอาการ"
              },
              {
                  "question_id":7,
                  "answer":true,
                  "rew_answer":"ไม่มีอาการ"
              }
          ]
          
      },
    })
      .then(function (res) {
        console.log("Success to Save Data!", res);
        Router.push("/page.covidcomplete");
      })
      .catch((error) => {
        console.log("Not found Token or PatientNo", error.response.status);
        if (error.response.status === 401) {
          Axios({
            method: "GET",
            url: "http://34.87.54.114:8003/api/v1/manage/token",
            headers: {
              Authorization: `Bearer ${localStorage.getItem("jwt-refresh")}`,
            },
          })
            .then((res) => {
              localStorage.setItem("jwt", res.data["accept-token"]);
              console.log("Update Token Success!");
              setisLoading(false);
            })
            .catch((error) => {
              console.log("Error Code", error.response.status);
              Router.push("/page.covid1");
            });
        }
        if (error.response.status === 404) {
          console.log("ไม่มีข้อมูลอยู่ในระบบ");
          Router.push("/page.covid2");
        }
      });
  };


  //====================================================
  if (isLoading) {
    if (isEnd) {
      console.log("Profile", [Name, SSID, BirdDay, PostId]);
      console.log(
        "Question",
        currentQuestion,
        questionNo
      );
      //Router.push("/page.covidcomplete");
      if (!CheckSuccess) {
        saveDataHandler();
      }
      return null;
    } else {
      return (
        <FormItem>
          <div className="nu-font-covid">
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                width: "100vw",
                height: "100vh",
              }}
            >
              <div
                style={{
                  flexBasis: "100px",
                  height: "100%",
                }}
              >
                <HeaderCovid />
              </div>

              <div
                style={{
                  flexBasis: "auto",
                  height: "100%",
                  display: "flex",
                  flexDirection: "row",
                }}
              >
                <div
                  style={{ flexBasis: "10%", width: "100%", margin: "auto" }}
                >
                  <div>
                    <Button shape="circle">{"<"}</Button>
                  </div>
                </div>
                <div
                  style={{
                    background: "",
                    flexBasis: "auto",
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    flexDirection: "column",
                  }}
                >
                  <div
                    style={{
                      flexBasis: "auto",
                      display: "flex",
                      width: "100%",
                      height: "100%",
                      alignItems: "center",
                      flexDirection: "column",
                      padding: "5%",
                      justifyContent: "center",
                    }}
                  >
                    <div
                      style={{
                        width: "100%",
                        height: "100%",
                        display: "flex",
                        alignItems: "flex-end",
                        justifyContent: "center",
                        padding: "0px 20%",
                      }}
                    >
                      <AudioPlayer
                        url={options}
                        token={localStorage.getItem("jwt")}
                        isLoading={isLoading}
                      />
                      <p />
                      <QuestionCovid text={question} check={token} />
                    </div>
                    <div
                      style={{
                        width: "100%",
                        height: "100%",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                    >
                      {showResults ? <ShowButton /> : null}
                    </div>
                  </div>
                </div>
                <div
                  style={{ flexBasis: "10%", width: "100%", margin: "auto" }}
                >
                  <div style={{ textAlign: "right" }}>
                    <Button shape="circle">{">"}</Button>
                  </div>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  width: "100%",
                  height: "auto",
                  alignItems: "center",
                  justifyContent: "center",
                  flexDirection: "row",
                }}
              >
                <div
                  style={{ width: "100%", flexBasis: "auto", height: "100%" }}
                >
                  <div
                    style={{
                      width: "auto",
                      display: "flex",
                      justifyContent: "center",
                      height: "100%",
                      alignItems: "flex-end",
                    }}
                  >
                    <div className="nu-box-covid1" style={{ marginLeft: "8%" }}>
                      {page.map((page, index) => {
                        if (index === currentQuestion) {
                          return (
                            <span
                              className="nu-box1-covid1"
                              style={{ background: "white" }}
                            />
                          );
                        }
                        return <span className="nu-box1-covid1" />;
                      })}
                    </div>
                  </div>
                </div>
                <div
                  style={{
                    flexBasis: "auto",
                  }}
                >
                  <Button
                    shape="circle"
                    size="middle"
                    style={{
                      width: "53px",
                      height: "53px",
                      marginRight: "25px",
                    }}
                  >
                    <img src={img1} style={{ width: "auto", height: "auto" }} />
                  </Button>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  justifyContent: "flex-end",
                }}
              >
                <h5
                  style={{
                    fontSize: "10px",
                    marginRight: "9px",
                    color: "#FFFFFF80",
                    marginBottom: "2px",
                  }}
                >
                  <Duration seconds={time} />
                </h5>
              </div>
              <div>
                <div className="w3-light-grey">
                  <Box />
                </div>
              </div>
              <div
                style={{
                  flexBasis: "180px",
                  height: "100%",
                }}
              >
                <div className="sound-icon disabled">
                  <div className="sound-wave">
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                    <i className="bar"></i>
                  </div>
                </div>
              </div>
              <div
                style={{
                  flexBasis: "auto",
                  display: "flex",
                  justifyContent: "center",
                  flexDirection: "row",
                  width: "100%",
                }}
              >
                <h5 style={{ color: "#FFFFFF80" }}>
                  Copyright © All rights reserved. 2020 มหาวิทยาลัยนเรศวร
                  Naresuan University
                </h5>
              </div>
            </div>
          </div>
        </FormItem>
      );
    }
  } else {
    return null;
  }
}

export default CovidQuestion;
