import React from "react";
import Webcam from "react-webcam";
import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
} from "antd";
const FormItem = Form.Item;

const WebcamCapture = (props) => {
  const webcamRef = React.useRef(null);
  const [imgSrc, setImgSrc] = React.useState(null);
  const capture = React.useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
    alert("กรุณาถอดหน้ากาก");
  }, [webcamRef, setImgSrc]);

  useEffect(() => {
      const Command = "Errormask"
      if(Command === "Errormask") {
        alert("กรุณาถอดหน้ากาก");
      }
  }, [imgSrc])

  return (
    <div>
      <FormItem>
          <div>
        <Webcam audio={false} ref={webcamRef} screenshotFormat="image/jpeg" style={{borderRadius:"20px"}} /></div>
        <Button onClick={capture} style={{width:"100%"}} type="primary">Capture photo</Button>
        {/*<div><img src={imgSrc} /></div>*/}
        
      </FormItem>
    </div>
  );
};

export default WebcamCapture;
