import React, { useEffect, useState } from "react";
import Webcam from "react-webcam";
import {
  Form,
  Select,
  InputNumber,
  DatePicker,
  Switch,
  Slider,
  Button,
} from "antd";
import { useDispatch, useSelector } from "react-redux";
import {
  updateFirstName,
  updateLastName,
  updateSsid,
} from "../../store/actions/postAction";
import img1 from "../../public/1.png";
import Router from "next/router";
import Axios from "axios";

const FormItem = Form.Item;

const WebcamCapture = () => {
  const dispatch = useDispatch();
  const webcamRef = React.useRef(null);
  const [imgSrc, setImgSrc] = React.useState(null);
  const { SSID,Token } = useSelector((state) => state.post);
  const [token, setToken] = useState(null);
  const [isLoading, setisLoading] = useState(false);

  useEffect(() => {
    setToken(localStorage.getItem("jwt"))
    console.log("setSuccess")
    setToken(setisLoading(true))
  },[]);

  const Axiosphoto = (value) => {
    const Command = value;
    console.log(Command);
    if (Command === "Errormask") {
      alert("กรุณาถอดหน้ากาก");
    } else if (Command === "FoundData") {
      const Data = ["3640600077526", "สมชาย", "หมายปอง"];
      dispatch(updateSsid(Data[0]));
      dispatch(updateFirstName(Data[1]));
      dispatch(updateSsid(Data[2]));
      alert("พบข้อมูล");
      Router.push("/page.covid4");
    } else if (Command === "NotFoundData") {
      alert("ไม่พบข้อมูล,กรุณาโชว์บัตรประชาชนแทน");
    }
  };
  function taskOne() {
    return new Promise(async (resolve) => {
      const A = async () => {
        await console.log("Token", Token);
        const imageSrc = await webcamRef.current.getScreenshot();
        await console.log(imageSrc);
        var block = await imageSrc.split(";");
        var contentType = await block[0].split(":")[1];
        var realData = await block[1].split(",")[1];
        var blob = await b64toBlob(realData, contentType);
        resolve(blob);
      };
      await A();
    });
  }
  function taskTwo(value) {
    return new Promise(async (resolve) => {
      const B = async () => {
        await console.log("A", value);
        const formData = await new FormData();
        await formData.append("picture", value);
        return formData;
      };
      const postimage = async () => {
        await Axios.post(
          "http://34.87.54.114:8003/api/v1/file/uploadphoto",
          formData,
          {
            headers: {
              Authorization: `Bearer ${Token}`,
              "Content-Type": "multipart/form-data",
            },
          }
        )
          .then((res) => {
            console.log("success", res);
            Axiosphoto("Errormask");
          })
          .catch((err) => {
            console.log("err", err);
          });
      };
      var formData = await B();
      await postimage();
    });
  }

  const main = async () => {
    var result = await taskOne();
    await taskTwo(result);
  };

  const capture = React.useCallback(async () => {
    main();
  }, [webcamRef, setImgSrc]);

  /*const [statee, setStatee] = useState(null);
  const fileSelectedHandler = (event) => {
    setStatee(event.target.files[0]);
    console.log(event.target.files[0]);
  };
  const fileUploadHandler = () => {
    console.log("TEST", statee);
    const fd = new FormData();
    fd.append("picture", statee);
    Axios.post("http://34.87.54.114:8003/api/v1/file/uploadphoto", fd, {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((res) => {
        console.log("success", res);
      })
      .catch((err) => {
        console.log("err", err);
      });
  };*/

  function b64toBlob(b64Data, contentType, sliceSize) {
    contentType = contentType || "";
    sliceSize = sliceSize || 512;
    var byteCharacters = atob(b64Data);
    var byteArrays = [];
    for (var offset = 0; offset < byteCharacters.length; offset += sliceSize) {
      var slice = byteCharacters.slice(offset, offset + sliceSize);
      var byteNumbers = new Array(slice.length);
      for (var i = 0; i < slice.length; i++) {
        byteNumbers[i] = slice.charCodeAt(i);
      }
      var byteArray = new Uint8Array(byteNumbers);
      byteArrays.push(byteArray);
    }
    var blob = new Blob(byteArrays, { type: contentType });
    return blob;
  }

  /*const test = () => {
    var ImageURL = data;
    console.log(ImageURL);
    var block = ImageURL.split(";");
    var contentType = block[0].split(":")[1];
    var realData = block[1].split(",")[1];
    var blob = b64toBlob(realData, contentType);
    console.log(blob);
    const fd = new FormData();
    fd.append("picture", blob);
    Axios.post("http://34.87.54.114:8003/api/v1/file/uploadphoto", fd, {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((res) => {
        console.log("success", res);
      })
      .catch((err) => {
        console.log("err", err);
      });
  };*/
  if (isLoading) {
    return (
      <div>
        <FormItem>
          <div>
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              style={{ borderRadius: "20px" }}
            />
          </div>
          <Button onClick={capture} style={{ width: "100%" }} type="primary">
            Capture photo
          </Button>
          <div>
            <img src={imgSrc} />
            {/*<input type="file" onChange={fileSelectedHandler} />
          <button onClick={fileUploadHandler}>Upload</button>
          <button onClick={test}>Upload TEST</button>*/}
          </div>
        </FormItem>
      </div>
    );
  } else {
    console.log("Loading....");
    return null;
  }
};

export default WebcamCapture;
